mapper.select:
INSERT
  INTO TBL_MEMBER A
(
  A.MEMBER_NO
, A.MEMBER_ID
, A.MEMBER_PWD
, A.MEMBER_NAME
, A.GENDER
, A.EMAIL
, A.PHONE
, A.ADDRESS
, A.AGE
, A.ENROLL_DATE
)
VALUES
(
  SEQ_MEMBER_NO.NEXTVAL
, #{ memberId }
, #{ memberPwd }
, #{ memberName }
, #{ gender }
, #{ email }
, #{ phone }
, #{ address }
, #{ age }
, SYSDATE
)
